//  Injection logic 

let successCount = 0;

for (const theme of themes) {
  const filePath = path.join(BASE, theme.name, 'en.ts');
  let content = fs.readFileSync(filePath, 'utf8');

  // Detect line endings
  const nl = content.includes('\r\n') ? '\r\n' : '\n';

  // Build injection block
  const injection = buildInjection(theme, nl);

  // Pattern: end of faq array ], then close third-grade }, then close gradeContent }
  const pattern = /(      \],)\r?\n(    \},)\r?\n(  \},)/;

  if (!pattern.test(content)) {
    console.error(`ERROR: Injection pattern not found in ${theme.name}/en.ts`);
    process.exit(1);
  }

  // Check if already injected (look within third-grade section  last grade, so check to end of file)
  const tgIdx = content.indexOf("'third-grade':");
  if (tgIdx !== -1) {
    const thirdGradeSection = content.substring(tgIdx);
    if (thirdGradeSection.includes("uniqueGradeAngle:")) {
      console.log(`SKIP: ${theme.name}/en.ts already has uniqueGradeAngle in third-grade section`);
      continue;
    }
  }

  content = content.replace(pattern, `$1${nl}${injection}${nl}$2${nl}$3`);

  fs.writeFileSync(filePath, content, 'utf8');
  successCount++;
  console.log(`OK: Injected 6 third-grade fields into ${theme.name}/en.ts`);
}

console.log(`\nDone: ${successCount} theme(s) enriched.`);
